import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Tasks.css";

interface Task {
    id: number;
    title: string;
    description: string;
    isComplete: boolean;
}

const Tasks = () => {
    const [tasks, setTasks] = useState<Task[]>([]);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [editingTask, setEditingTask] = useState<Task | null>(null);
    const navigate = useNavigate();
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (!token) {
            navigate("/");
            return;
        }
        fetchTasks();
    }, []);

    // Fetch tasks from API
    const fetchTasks = async () => {
        try {
            const response = await axios.get("http://localhost:5000/tasks", {
                headers: { Authorization: token },
            });
            setTasks(response.data);
        } catch (error) {
            console.error("Error fetching tasks", error);
        }
    };

    // Create new task
    const handleCreateTask = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            await axios.post(
                "http://localhost:5000/tasks",
                { title, description },
                { headers: { Authorization: token } }
            );
            setTitle("");
            setDescription("");
            fetchTasks(); // Refresh tasks
        } catch (error) {
            console.error("Error creating task", error);
        }
    };

    // Toggle task completion (Mark as complete or undo)
    const handleToggleTask = async (id: number) => {
        try {
            await axios.put(`http://localhost:5000/tasks/${id}`, {}, { headers: { Authorization: token } });
            fetchTasks(); // Refresh tasks
        } catch (error) {
            console.error("Error updating task", error);
        }
    };

    // Delete task
    const handleDeleteTask = async (id: number) => {
        try {
            await axios.delete(`http://localhost:5000/tasks/${id}`, { headers: { Authorization: token } });
            fetchTasks(); // Refresh tasks
        } catch (error) {
            console.error("Error deleting task", error);
        }
    };

    // Edit task (set form to editing mode)
    const handleEditTask = (task: Task) => {
        setEditingTask(task);
        setTitle(task.title);
        setDescription(task.description);
    };

    // Update task in database
    const handleUpdateTask = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!editingTask) return;
        try {
            await axios.put(
                `http://localhost:5000/tasks/${editingTask.id}`,
                { title, description },
                { headers: { Authorization: token } }
            );
            setEditingTask(null);
            setTitle("");
            setDescription("");
            fetchTasks(); // Refresh tasks
        } catch (error) {
            console.error("Error updating task", error);
        }
    };

    return (
        <div className="tasks-container">
            <h2>Task Manager</h2>
            <button className="logout-button" onClick={() => { localStorage.removeItem("token"); navigate("/"); }}>Logout</button>

            {/* Task Form (Add or Edit) */}
            <form className="task-form" onSubmit={editingTask ? handleUpdateTask : handleCreateTask}>
                <input
                    type="text"
                    placeholder="Task Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Task Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <button type="submit">{editingTask ? "Update Task" : "Add Task"}</button>
                {editingTask && <button type="button" onClick={() => setEditingTask(null)}>Cancel</button>}
            </form>

            {/* To Do Tasks */}
            <h3>To Do</h3>
            <ul className="task-list">
                {tasks.filter(task => !task.isComplete).map((task) => (
                    <li key={task.id} className="task-item">
                        <span>{task.title} - {task.description}</span>
                        <button onClick={() => handleToggleTask(task.id)}>Complete</button>
                        <button onClick={() => handleEditTask(task)}>Edit</button>
                        <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {/* Completed Tasks */}
            <h3>Completed</h3>
            <ul className="task-list completed-list">
                {tasks.filter(task => task.isComplete).map((task) => (
                    <li key={task.id} className="task-item completed">
                        <span>{task.title} - {task.description}</span>
                        <button onClick={() => handleToggleTask(task.id)}>Undo</button>
                        <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Tasks;
