import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Tasks.css";

interface Task {
    id: number;
    title: string;
    description: string;
    isComplete: boolean;
}

const Tasks = () => {
    const [tasks, setTasks] = useState<Task[]>([]);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [editingTask, setEditingTask] = useState<Task | null>(null);
    const navigate = useNavigate();
    const token = localStorage.getItem("token");

    useEffect(() => {
        fetchTasks();
    }, []);


    const fetchTasks = async () => {
        try {
            const response = await axios.get("http://localhost:5000/tasks", {
                headers: { Authorization: token },
            });
            setTasks(response.data);
        } catch (error) {
            console.error("Error fetching tasks", error);
        }
    };

    // Create or Update a Task
    const handleTaskSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        if (editingTask) {
            try {
                const taskToUpdate = tasks.find(task => task.id === editingTask.id);
                if (!taskToUpdate) return; // Ensure task exists

                await axios.put(
                    `http://localhost:5000/tasks/${editingTask.id}`,
                    { 
                        title, 
                        description, 
                        isComplete: taskToUpdate.isComplete  // Preserve completed status
                    },
                    { headers: { Authorization: token } }
                );

                // Update the local state directly without fetching from the server
                setTasks(prevTasks =>
                    prevTasks.map(task =>
                        task.id === editingTask.id ? { ...task, title, description } : task
                    )
                );

                setEditingTask(null);
                setTitle("");
                setDescription("");
            } catch (error) {
                console.error("Error updating task", error);
            }
        } else {
            // Add new task
            try {
                const response = await axios.post(
                    "http://localhost:5000/tasks",
                    { title, description },
                    { headers: { Authorization: token } }
                );
                setTasks(prevTasks => [...prevTasks, response.data]); // Add the new task to the local state
                setTitle("");
                setDescription("");
            } catch (error) {
                console.error("Error creating task", error);
            }
        }
    };


    // Edit Task (Populates the Add Task Form)
    const handleEditTask = (task: Task) => {
        setEditingTask(task);
        setTitle(task.title);
        setDescription(task.description);
    };

    // Toggle task completion
    const handleToggleTask = async (id: number, currentStatus: boolean) => {
        try {
            await axios.put(
                `http://localhost:5000/tasks/${id}`,
                { isComplete: !currentStatus },
                { headers: { Authorization: token } }
            );

            // ✅ Update UI immediately without waiting for re-fetch
            setTasks(prevTasks =>
                prevTasks.map(task =>
                    task.id === id ? { ...task, isComplete: !currentStatus } : task
                )
            );
        } catch (error) {
            console.error("Error updating task", error);
        }
    };



    // Delete Task
    const handleDeleteTask = async (id: number) => {
        try {
            await axios.delete(`http://localhost:5000/tasks/${id}`, { headers: { Authorization: token } });
            setTasks(tasks.filter(task => task.id !== id)); // Remove from UI instantly
        } catch (error) {
            console.error("Error deleting task", error);
        }
    };

    return (
        <div className="tasks-container">
            <h2>Task Manager</h2>

            {/* Task Form: Used for Adding and Editing */}
            <form className="task-form" onSubmit={handleTaskSubmit}>
                <input
                    type="text"
                    placeholder="Task Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Task Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <button type="submit">{editingTask ? "Update Task" : "Add Task"}</button>
                {editingTask && <button type="button" onClick={() => setEditingTask(null)}>Cancel</button>}
            </form>

            {/* To Do Tasks */}
            {/* To Do Tasks */}
            {/* To Do Tasks */}
            <h3>To Do</h3>
            <ul className="task-list">
                {tasks.filter(task => !task.isComplete).map((task) => (
                    <li key={task.id} className="task-item">
                        <div className="task-info">
                            <strong>{task.title}</strong>
                            <p>{task.description}</p>
                        </div>
                        <button onClick={() => handleToggleTask(task.id, task.isComplete)}>Complete</button>
                        <button onClick={() => handleEditTask(task)}>Edit</button>
                        <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {/* Completed Tasks */}
            <h3>Completed</h3>
            <ul className="task-list completed-list">
                {tasks.filter(task => task.isComplete).map((task) => (
                    <li key={task.id} className="task-item">
                        <div className="task-info">
                            <strong>{task.title}</strong>
                            <p>{task.description}</p>
                        </div>
                        <button onClick={() => handleToggleTask(task.id, task.isComplete)}>Undo</button>
                        <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
                    </li>
                ))}
            </ul>


                    </div>
    );
};

export default Tasks;
